mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
FROM `like`
WHERE user_id = #{userId}
  AND post_id = #{postId}

mapper.delete:
DELETE FROM `like`
WHERE user_id = #{userId}
  AND post_id = #{postId}

mapper.select:
SELECT l.post_id AS post_id FROM `like` AS l JOIN post AS p ON l.post_id = p.id WHERE l.user_id = #{userId} AND p.is_deleted = 0
AND (l.create_date < ( SELECT create_date FROM `like` WHERE post_id = #{lastId} LIMIT 1 ) OR (l.create_date = ( SELECT create_date FROM `like` WHERE post_id = #{lastId} LIMIT 1 ) AND l.post_id > #{lastId}))
ORDER BY l.create_date DESC, l.post_id ASC LIMIT #{size}
